import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect, useState } from 'react';
import { addFavorite, deleteFavorite } from '../../redux/actions';
import style from './Card.module.css';

export default function Card({ name, gender, onClose, species, image, id }) { // Ac√° Card recibe por p√°r√°metro props y hago destructuring. Los valores de estas propiedades le llegan desde App: props --> {name: '', species: '', gender: '', image: '', onClose: fn}
   const dispatch = useDispatch();

   const myFavorites = useSelector(state => state.myFavorites)

   const [isFav, setIsFav] = useState(false);

   const handleFavorite = () => {
      if(isFav){
         setIsFav(false);
         dispatch(deleteFavorite(id))
      }
      else{
         setIsFav(true);
         dispatch(addFavorite({ name, gender, onClose, species, image, id })) // le paso props por destructuring porque as√≠ lo puse en Card
      }
   }

   useEffect(() => {
      myFavorites.forEach((fav) => {
         if(fav.id === id) { // Si no hubiera hecho destructuring en Card, ac√° ser√≠a props.id en lugar de id.
            setIsFav(true);
         }
      });
   }, [myFavorites, id]); // React me hace incluir id en el array de dependencias, sino me da error.

   return (
      <div className={style.card}>
         <div className={style.fav}>
            {
               isFav ? (
                  <button onClick={handleFavorite}>‚ù§Ô∏è</button>
               ) : (
                  <button onClick={handleFavorite}>ü§ç</button>
               )
            }

            <div className={style.btn}>
               <button onClick={onClose}>‚ùé</button>
            </div>
            
         </div>

         <div className={style.front}>
            <img  src={image} alt={name} />
         </div>

         <div className={style.back}>

            <div>
               <Link to={`/detail/${id}`} className={style.link}>
                  <h2 className={style.name}>{name} </h2>
               </Link>
            </div>

            <div className={style.characteristics}>
               <h2>Specie: {species} </h2>
               <h2>Gender: {gender} </h2>
            </div>

         </div>
      </div>
   );
}
